INSERT INTO `indexfood` VALUES (25, '土豆丝卷饼', 'http://localhost:8080/img/recipeImg/zaocan1.jpg', '面粉,土豆丝', 4, 8, 10, '煎', '香辣味', '15分钟', '初级入门', '100克,20克', '3人份', '食用油,辣椒酱', '3毫升,适量', '土豆丝卷饼是一道家常菜，制作原料主要有土豆、彩椒、蒜苗等，口味鲜美，营养丰富', 'http://localhost:8080/img/successImg/success25.jpg', '营养丰富，告别饥饿');
INSERT INTO `indexfood` VALUES (26, '土豆饼', 'http://localhost:8080/img/recipeImg/zaocan2.jpg', '土豆条,中筋面粉', 6, 8, 53, '煎', '家常味', '15分钟', '初级入门', '100克,50克', '3人份', '葱花,盐,十三香,胡椒粉,食用油', '5克,3克,2克,2克,5毫升', '土豆饼加鸡蛋和不加鸡蛋口感不太一样。不加鸡蛋的有土豆丝的原香。可以根据个人喜好选择加或者不加。', 'http://localhost:8080/img/successImg/success26.jpg', '美味营养，吃完元气满满');
INSERT INTO `indexfood` VALUES (27, '营养早餐烤鸡蛋', 'http://localhost:8080/img/recipeImg/zaocan3.jpg', '鸡蛋', 4, 8, 12, '烤', '家常味', '30分钟', '初级入门', '4个', '2人份', '毛豆,培根,胡椒粉,椒盐粉,番茄酱,盐', '适量,适量,适量,适量,适量,适量', '突然想到以前放学路上，街边卖的烤鸡蛋，可是已经很久没有看到过了，今天就凭着记忆试做一下，感觉还不错呢，除了有点老，味道还好，哈！营养哇，做早餐一流呢！', 'http://localhost:8080/img/successImg/success27.jpg', '简单美味，几分钟搞定早餐');
INSERT INTO `indexfood` VALUES (28, '金色馒头片', 'http://localhost:8080/img/recipeImg/zaocan4.jpg', '馒头,鸡蛋', 8, 8, 20, '煎', '家常味', '10分钟', '新手尝试', '1个,2个', '2人份', '橄榄油,椒盐', '10勺,1/2勺', '金色的阳光金色的你\r\n早上醒来，就要迎接忙碌的一天了。当餐桌上投下一缕温暖的金色阳光时，一家人享受着你营养快捷的早餐美食，这是多么的满足和幸福。来来来，一起来分享这款简单易做，香酥松脆，色泽金黄的炸馒头片吧，大人小孩都喜欢。', 'http://localhost:8080/img/successImg/success28.jpg', '经典家常，上桌就光盘');
INSERT INTO `indexfood` VALUES (29, '银耳红枣汤', 'http://localhost:8080/img/recipeImg/zaocan5.jpg', '银耳,红枣,枸杞', 3, 8, 50, '煮', '咸鲜味', '10分钟', '初级入门', '1个,2克,10克', '1人份', '水,冰糖', '1升,15克', '银耳红枣汤是以银耳和红枣为主要食材制成的一款家常汤品，可适当添加莲子、枸杞子等配料。具有宁心安神、益智健脑、增强食欲功效。', 'http://localhost:8080/img/successImg/success29.jpg', '色香味俱佳，惊艳味蕾');
INSERT INTO `indexfood` VALUES (30, '竹笋炒腊肉', 'http://localhost:8080/img/recipeImg/wucan1.jpg', '竹笋,腊肉', 7, 9, 20, '炒', '香辣味', '10分钟', '中级掌勺', '500克,150克', '5人份', '油,盐,酱油,红辣椒,蒜', '6毫升,3克,1毫升,2个,1个', '竹笋是大自然给人类的礼物馈赠，竹笋的营养丰富，含膳食纤维高、低热量、是减肥养生的好食材。竹笋能凉拌、炒、煲汤、还能晒干。最喜欢常吃的还是竹笋炒腊肉，让腊肉的香融入到竹笋中，口口嫩香。', 'http://localhost:8080/img/successImg/success30.jpg', '营养美味，一吃停不下来');
INSERT INTO `indexfood` VALUES (31, '五彩皮冻', 'http://localhost:8080/img/recipeImg/wucan2.jpg', '猪肉皮,土豆,胡萝卜,青豆,黄豆,木耳', 3, 9, 50, '煮', '咸鲜味', '60分钟', '初中水平', '450克,80克,50克,80克,80克,30克', '5人份', '盐,香叶,桂皮,大料,花椒,葱姜,老抽,生抽,白糖', '5克,4克,5克,5克,4克,10克,15毫升,10毫升,10克', '喜欢吃肉皮冻，更喜欢食材多多，色彩斑斓，营养丰富的五彩皮冻。用买整块肉绞肉馅片下来的肉皮，做了这好看好吃，营养全面，尤其适合春季食用的五彩皮冻。', 'http://localhost:8080/img/successImg/success31.jpg', '香气扑鼻，好吃过瘾');
INSERT INTO `indexfood` VALUES (32, '金平牛蒡', 'http://localhost:8080/img/recipeImg/wucan3.jpg', '牛蒡,胡萝卜', 3, 9, 15, '炒', '家常味', '5分钟', '新手尝试', '一根,半根', '2人份', '植物油,生抽,白糖,白芝麻,葱花', '适量,少许,少许,少许,少许', '牛蒡是一种营养价值极高的蔬菜，它不仅富含膳食纤维，而且富含多种营养素，多酚类物质，经常食用牛蒡，能够起到促进消化、减脂瘦身、降血糖、降血压、降血脂、保肝护肝等等作用。金平是日式料理中常用的一种烹饪方法，有炒制的意思。', 'http://localhost:8080/img/successImg/success32.jpg', '香气四溢，吃出幸福感');
INSERT INTO `indexfood` VALUES (33, '法式芝士香蕉可丽饼', 'http://localhost:8080/img/recipeImg/xiawucha1.jpg', '低筋面粉,牛奶,酵母,糖,盐,玉米油', 3, 10, 100, '煎', '奶香味', '5分钟', '初中水平', '50克,100克,3克,6克,2克,少许', '1人份', '百吉福芝士,香蕉', '1片,1个', '以前做过的香蕉可丽饼没有加芝士，这次的简直香味爆棚啊！\r\n依然是无泡打粉的健康版，这个作为早餐营养全面了，有鸡蛋、牛奶、芝士、香蕉、碳水化合物。\r\n芝士就是香，确实与众不同，加入了芝士的可丽饼营养和香味都增加了不少。', 'http://localhost:8080/img/successImg/success33.jpg', '美味下午茶，让人欲罢不能');
INSERT INTO `indexfood` VALUES (34, ' \r\n山楂雪丽球', 'http://localhost:8080/img/recipeImg/xiawucha2.jpg', '山楂,白砂糖,吉利丁片', 3, 10, 43, '炒', '酸甜味', '2小时', '新手尝试', '500克,250克,5克', '5人份', '糖粉', '20克', '山楂雪丽球，小时候常见的一款零食，酸酸甜甜的，小小一个，一罐能吃上小半天。家里的5斤山楂终于做完了，今年应该是不会再做山楂的零食了', 'http://localhost:8080/img/successImg/success34.jpg', '香味浓郁，让人食欲大增');
INSERT INTO `indexfood` VALUES (35, '旺旺旺红豆吐司', 'http://localhost:8080/img/recipeImg/xiawucha3.jpg', '高筋面粉,牛奶,水,炼奶,鸡蛋,盐', 5, 10, 63, '烘培', '甜味', '60分钟', '中级掌勺', '200克,35克,50克,10克,25克,2克', '3人份', '杏仁,巧克力', '6个,9个', '红豆具有清热解毒、健脾益胃、利尿消肿、通气除烦、补血生乳等多种功效。\r\n我会经常熬一些红豆放在冰箱随吃随拿，红豆可以做成蜜红豆也可以做成红豆沙', 'http://localhost:8080/img/successImg/success35.jpg', '味道鲜美，瞬间打开胃口');
INSERT INTO `indexfood` VALUES (36, '蒸苜蓿麦饭', 'http://localhost:8080/img/recipeImg/wancan1.jpg', '苜蓿,玉米面,小米面,中筋面粉', 3, 11, 23, '蒸', '香辣味', '5分钟', '初中水平', '500克,60克,60克,80克', '2人份', '小葱,辣椒面,蒜泥,芝麻,盐,糖,十三香', '适量,5克,适量,少许,3克,1克,3克', '少油少盐，低热量低脂肪的食物\r\n春来野菜香，野菜很受人们的喜爱，目前已有荠荠菜，苜蓿，白蒿，茵陈，还有“树上蔬菜”香椿等已经上市。苜蓿也叫草头，可清炒，凉拌，包饺子，煎鸡蛋饼，额娘最喜欢用来蒸麦饭，洗净拌上各种面粉，一蒸一炒，拌上油泼辣子蒜泥汁，入口细润，香软可口，少油少盐，低热量低脂肪的素食，太美味啦！喜欢的亲抓紧吃，错过了季节，又要等一年哦', 'http://localhost:8080/img/successImg/success36.jpg', '味道超赞，无比解馋');
INSERT INTO `indexfood` VALUES (37, '营养杂粮粥', 'http://localhost:8080/img/recipeImg/wancan2.jpg', '杂粮米', 8, 11, 53, '煮', '家常味', '60分钟', '中级掌勺', '120克', '2人份', '红糖', '50克', '作为上班族，朝九晚五那是必须的；作为设计类的加班族，加班熬夜也是必须的。虽然我不习惯吃夜宵，但总有吃夜宵的时候。尤其是加班到深夜时，饥肠辘辘啊，恨不得来碗热腾腾的面条、香喷喷的热粥~\r\n但我最爱的宵夜还是热乎乎、香喷喷的粥~~', 'http://localhost:8080/img/successImg/success37.jpg', '味道鲜美，瞬间打开胃口');
INSERT INTO `indexfood` VALUES (38, '砂锅烤红薯', 'http://localhost:8080/img/recipeImg/wancan3.jpg', '红薯', 4, 11, 3, '烤', '甜味', '60分钟', '新手尝试', '5个', '3人份', '锡纸', '2张', '又到一年红薯季，邻居送来了好多的红薯，孩子说想吃烤红薯，正好最近入手了一款砂锅，据说可以经得起800度的高温，还可以干烧，于是我就拿它来烤了个红薯，漂亮吧！', 'http://localhost:8080/img/successImg/success38.jpg', '均衡营养，滋补养生');
INSERT INTO `indexfood` VALUES (39, '葱香玉米烙', 'http://localhost:8080/img/recipeImg/yexiao1.jpg', '玉米片', 7, 12, 76, '炸', '家常味', '30分钟', '中级掌勺', '200克', '2人份', '盐,五香粉,植物油,小葱', '2勺,1勺,1勺,1根', '为了满足肠胃，为了身体健康，今天给大家分享一道美味小吃【葱香玉米烙】。做法非常简单，用时短，分分钟搞定！调味可甜可咸，营养低脂。不仅能起到磨牙、抗饥的作用，还能预防心脏病、癌症、延缓衰老、防止皮肤病变、减轻动脉硬化、对抗眼睛老化，增强人的脑力和记忆力！别小瞧玉米，它可是主食界里营养价值、保健作用最高的！嗯，就酱紫！', 'http://localhost:8080/img/successImg/success39.jpg', '香味浓郁，让人食欲大增');
INSERT INTO `indexfood` VALUES (40, '糖不甩', 'http://localhost:8080/img/recipeImg/yexiao2.jpg', '糯米粉', 2, 12, 75, '煮', '甜味', '10分钟', '新手尝试', '120克', '2人份', '温水,红糖,熟花生', '一碗,一勺,一把', '小吃街上永远都少不了一份美味的糖不甩，其实它本来是属于广东的一道经典甜品，因做法简单，开胃又不腻，香味悠远逐渐发扬到全国各地的小吃街，糖不甩如今已不是什么高大上的点心了，自己在家就可以简单的做出来。', 'http://localhost:8080/img/successImg/success40.jpg', '香气扑鼻，好吃过瘾');
INSERT INTO `indexfood` VALUES (41, '油炸臭豆腐', 'http://localhost:8080/img/recipeImg/yexiao3.jpg', '臭豆腐', 7, 12, 13, '炸', '咸鲜味', '10分钟', '新手尝试', '1盒', '4人份', '油,蒜蓉辣椒酱,甜面酱', '50毫升,少许,少许', '臭豆腐是一种极具特色的中华传统小吃，古老而传统，我们这里有一句老话，叫做“臭发鲜”也就是闻闻臭的要命，入口却鲜的让人令人欲罢不能。臭豆腐可蒸可炸，这是一道油炸臭豆腐，需要二次炸制过程才会外脆里嫩，原本有一点咸味，蘸上各式的酱料，味道更佳', 'http://localhost:8080/img/successImg/success41.jpg', '鲜香美味，让人口水直流');
