INSERT INTO `recipearitice` VALUES (1, '无油可乐鸡翅', 'http://localhost:8080/img/recipeImg/kuaishou1.jpg', '鸡翅,无糖可乐', 3, 1, 25, '烧', '咸鲜味', '30分钟', '初级入门', '10个,一瓶', '2人份', '葱姜,生抽,老抽,白酒,盐,八角,花椒', '适量,15毫升,5毫升,5毫升,适量,2个,适量', '可乐鸡翅，以鸡翅和可乐为主料制作而成。味道鲜美，色泽艳丽，鸡翅嫩滑，咸甜适中，又保留了可乐的香气，深受大众喜欢。 每次孩子回来，可乐鸡翅是必点的美味，这次做了无油版的可乐鸡翅，感觉油腻感减少了，口感更好！', 'http://localhost:8080/img/successImg/success1.jpg');
INSERT INTO `recipearitice` VALUES (2, '炒合菜', 'http://localhost:8080/img/recipeImg/kuaishou2.jpg', '鸡蛋,豆芽,韭菜,粉条,葱花', 3, 1, 45, '烧', '家常味', '15分钟', '新手尝试', '适量,适量,适量,适量,适量', '1人份', '生抽,耗油,食盐', '两勺,一勺,适量', '很简单的一款家常小炒好吃又下饭赶紧试试吧', 'http://localhost:8080/img/successImg/success2.jpg');
INSERT INTO `recipearitice` VALUES (3, '蒸鲈鱼', 'http://localhost:8080/img/recipeImg/kuaishou3.jpg', '鲈鱼,青红辣椒,姜丝', 3, 1, 43, '蒸', '家常味', '10分钟', '中级掌勺', '一条,各两个,适量', '3人份', '油,盐,生抽,蒜末', '适量,适量,适量,适量', '春节到了，猪肉牛肉都吃不动了，一天到晚就喜欢吃点清淡的，这段时间除了各种蔬菜，给家人补充营养的食物，我只选择鱼和虾，鱼虾的蛋白质丰富，脂肪少，冬天进补最合适不过了。\r\n\r\n不过，再有营养的东西，都要应时节才好，鱼也是一样。鱼的种类非常的多样，有的鱼适合冬天，有的鱼又适合夏天吃，天气寒冷的时候，最适合吃的就是鲈鱼了。冬天的鲈鱼是一年中最为鲜嫩肥美的时候，而且价格相对于其他时间也比较便宜，营养丰富，不容错过哦。', 'http://localhost:8080/img/successImg/success3.jpg');
INSERT INTO `recipearitice` VALUES (4, '西芹山药炒木耳', 'http://localhost:8080/img/recipeImg/kuaishou4.jpg', '西芹,山药,木耳', 8, 1, 178, '炒', '家常味', '5分钟', '新手尝试', '100克,1根,5克', '2人份', '油,盐,生抽,蒜', '适量,1勺,1勺,适量', '西芹山药炒木耳，做法简单，非常下饭。', 'http://localhost:8080/img/successImg/success4.jpg');
INSERT INTO `recipearitice` VALUES (5, '萝卜干煎蛋', 'http://localhost:8080/img/recipeImg/kuaishou5.jpg', '鸡蛋,萝卜干', 10, 1, 24, '煎', '家常味', '10分钟', '中级掌勺', '4个,30克', '4人份', '小葱,食用油,鸡粉', '1根,适量,1克', '这个菜，超级简单又好吃，成本低的你想偷笑。4个鸡蛋和一小把萝卜干，各种食材加起来不外乎8元钱左右。', 'http://localhost:8080/img/successImg/success5.jpg');
INSERT INTO `recipearitice` VALUES (6, '番茄炒牛肉', 'http://localhost:8080/img/recipeImg/kuaishou6.jpg', '牛肉,番茄', 8, 1, 35, '炒', '家常味', '10分钟', '未知', '300克,400克', '3人份', '大蒜,生抽,料酒,淀粉,姜,小葱', '10克,10毫升,5毫升,5克,5克,3克', '牛肉过年的时候，家里经常会做来吃，新的一年牛气十足，用番茄炒高是喜庆的颜色。', 'http://localhost:8080/img/successImg/success6.jpg');
INSERT INTO `recipearitice` VALUES (7, '辣子鸡丁', 'http://localhost:8080/img/recipeImg/xiafan1.jpg', '鸡丁,葱段,干辣椒', 8, 2, 42, '炒', '香辣味', '15分钟', '初级入门', '60克,5克,2克', '2人份', '姜,蒜,料酒,姜粉,盐,淀粉,油,花椒,生抽,糖', '2克,3瓣,1毫升,2克,3克,3克,50毫升,1克,3毫升,1克', '辣子鸡丁，鲜香麻辣~', 'http://localhost:8080/img/successImg/success7.jpg');
INSERT INTO `recipearitice` VALUES (8, '毛血旺', 'http://localhost:8080/img/recipeImg/xiafan2.jpg', '毛肚,鸭血,黄豆芽,宽粉，肥肠', 3, 2, 82, '煮', '香辣味', '60分钟', '初级入门', '250克,600克,500克,100,350克', '3人份', '料酒,食物油,葱段,花椒,姜片,蒜片,火锅底料,水', '15克,20克,8克,10克,10克,5克,150克,500毫升', '毛血旺是川菜的一道特色名菜，也是重庆江湖菜的鼻祖之一，已经列入国家标准委员会《渝菜烹饪标准体系》。', 'http://localhost:8080/img/successImg/success8.jpg');
INSERT INTO `recipearitice` VALUES (9, '酸辣土豆丝', 'http://localhost:8080/img/recipeImg/xiafan3.jpg', '土豆丝', 3, 2, 13, '炒', '酸辣味', '5分钟', '新手尝试', '300克', '2人份', '大蒜,生抽,料酒,淀粉,姜,小葱', '10克,10毫升,5毫升,5克,5克,3克', '酸辣土豆丝，是用土豆、辣椒、白醋，葱，姜等制作而成的菜肴，色泽光亮，酸辣可口。', 'http://localhost:8080/img/successImg/success9.jpg');
INSERT INTO `recipearitice` VALUES (10, '蒜苗回锅肉', 'http://localhost:8080/img/recipeImg/xiafan4.jpg', '蒜苗,五花肉', 3, 2, 78, '炒', '家常味', '30分钟', '新手尝试', '50克,100克', '1人份', '姜,葱,蒜', '5克,10克,8克', '蒜苗回锅肉是四川省和重庆市的一道传统名菜，属于川菜系。制作原料主要有猪肉、蒜苗等，此菜鲜香而辣美味可口，色香味俱佳。', 'http://localhost:8080/img/successImg/success10.jpg');
INSERT INTO `recipearitice` VALUES (11, '黄金咖喱虾饭团', 'http://localhost:8080/img/recipeImg/xiafan5.jpg', '咖喱酱,东北大米,鲜虾', 3, 2, 3, '炸', '家常味', '15分钟', '新手尝试', '50克,2杯,13个', '多人份', '鸡蛋,食用油,料酒', '1个,适量,3克', '香酥的黄金外壳，一口咬下，浓郁的米饭香夹杂着咖喱味，让人欲罢不能，细细嚼着，还能吃到爽脆的虾仁，太美味了，孩子开学了，快给孩子做一盘补充能量吧~', 'http://localhost:8080/img/successImg/success11.jpg');
INSERT INTO `recipearitice` VALUES (12, '麻辣豆腐', 'http://localhost:8080/img/recipeImg/xiafan6.jpg', '豆腐.瘦肉末', 3, 2, 108, '其他工艺', '香辣味', '30分钟', '初级入门', '300克,100克', '2人份', '大蒜,生抽,料酒,淀粉,姜,小葱', '10克,10毫升,5毫升,5克,5克,3克', '豆腐素有“植物肉”的美称，所以老少皆宜。', 'http://localhost:8080/img/successImg/success12.jpg');
INSERT INTO `recipearitice` VALUES (13, '清炒四季时蔬菜', 'http://localhost:8080/img/recipeImg/yingjishishu1.jpg', '紫甘蓝,藕片,芦笋,胡萝卜,木耳', 3, 3, 130, '炒', '家常味', '5分钟', '新手尝试', '100克,50克5,0克,30克3,0克', '2人份', '食盐,食用油,葱花', '适量,适量,5克', '每逢佳节胖三斤。胖了三斤又三斤。这春节小长假刚结束，马上又要开始正月十五闹元宵了。大鱼大肉一波一波的吃了又吃。特别需要来点清肠减肥的蔬菜，刮刮油，去去火。\r\n三月不减肥，月月徒伤悲哇！海吃海喝了10几天，再不减肥，春天来了，就来不及啦~抓上冰箱里的各种蔬菜，少油少盐的吃起来吧~低脂少油，多吃蔬菜，适量的肉类鱼类和主食才是健康减肥的王道！\r\n享瘦春天的阳光，从清炒时蔬开始吧~', 'http://localhost:8080/img/successImg/success13.jpg');
INSERT INTO `recipearitice` VALUES (14, '春季时蔬杂素汤', 'http://localhost:8080/img/recipeImg/yingjishishu2.jpg', '冬瓜,草菇', 5, 3, 20, '煮', '咸鲜味', '15分钟', '初中水平', '1个,3个', '1人份', '食盐,食用油,葱花', '适量,适量,5克', '薏米用清水泡发半小时@2@冬瓜去皮切正方块，然后切十字刀花@3@海茸蕊、薏米洗净沥干水份@4@竹笋去皮切段，草菇洗净切半备好@5@锅里放入冬瓜和薏米，加入5-6碗水，然后倒入香菇素高汤@6@煮至冬瓜透明，然后放入海茸', 'http://localhost:8080/img/successImg/success14.jpg');
INSERT INTO `recipearitice` VALUES (15, '四季时蔬西兰花', 'http://localhost:8080/img/recipeImg/yingjishishu3.jpg', '西兰花,胡萝卜', 5, 3, 0, '炒', '咸鲜味', '10分钟', '未知', '1个,500克', '3人份', '料酒,食物油,葱段,花椒,姜片,蒜片,火锅底料,水', '15克,20克,8克,10克,10克,5克,150克,500毫升', '大鱼大肉吃腻了总需要吃点清淡的调节一下～西兰花和胡萝卜的营养价值很高，对于久坐办公室的上班族来说，有护眼、排毒、刮油的功效', 'http://localhost:8080/img/successImg/success15.jpg');
INSERT INTO `recipearitice` VALUES (16, '夏季凉拌时蔬', 'http://localhost:8080/img/recipeImg/yingjishishu4.jpg', '番茄,卷心菜,黑木耳', 2, 3, 0, '拌', '酸甜味', '5分钟', '新手尝试', '1个,1个,2朵', '1人份', '食盐,食用油,葱花', '适量,适量,5克', '晚上搜刮冰箱，没多少东西了，就随手拌了个杂蔬，酸酸甜甜的，特别开胃，加了花生酱，味道层次更多了些。', 'http://localhost:8080/img/successImg/success16.jpg');
INSERT INTO `recipearitice` VALUES (17, '生菜肉末汤', 'http://localhost:8080/img/recipeImg/yingjishishu5.jpg', '高汤,生菜', 5, 3, 0, '煮', '家常味', '10分钟', '初中水平', '1碗,300克', '1人份', '油,盐,肉末', '1勺,1勺,50克', '这个季节，汤汤水水每天都少不了呢，可是咱们上班族，哪有那么多的时间天天煲汤呀，还好，美美学会偷懒了，周末煲上一大锅的排骨汤，上班时间随便弄点青菜叶子就是一道很有营养的汤汤啦！非常的赞哦。', 'http://localhost:8080/img/successImg/success17.jpg');
INSERT INTO `recipearitice` VALUES (18, '清炒小白菜', 'http://localhost:8080/img/recipeImg/yingjishishu6.jpg', '白菜', 7, 3, 0, '炒', '家常味', '5分钟', '初中水平', '半颗', '2人份', '食盐,食用油,葱花', '适量,适量,5克', '清炒大白菜，好吃又好看，家常必备，居家旅行必会的技能菜系。', 'http://localhost:8080/img/successImg/success18.jpg');
INSERT INTO `recipearitice` VALUES (19, '南瓜粥', 'http://localhost:8080/img/recipeImg/sushi1.jpg', '南瓜,大米', 4, 4, 0, '煮', '甜味', '60分钟', '初级入门', '100克,适量', '2人份', '水', '1.5升', '南瓜粥是以南瓜为主材制作而成的粥品。中医认为，南瓜性味甘、温，归脾、胃经，有补中益气、清热解毒之功效，适用于脾虚气弱、营养不良、肺痈、水火烫伤。', 'http://localhost:8080/img/successImg/success19.jpg');
INSERT INTO `recipearitice` VALUES (20, '双皮奶', 'http://localhost:8080/img/recipeImg/sushi2.jpg', '芒果,全脂牛奶,全脂奶粉', 6, 4, 0, '煮', '甜味', '60分钟', '初级入门', '半个,250克,20克', '1人份', '蛋清,白砂糖', '45克,15克', '双皮奶，是一种粤式甜品。清朝时起源自广东顺德，用水牛奶做原料。现遍布于广东、澳门、香港等地。', 'http://localhost:8080/img/successImg/success20.jpg');
INSERT INTO `recipearitice` VALUES (21, '炒茄子', 'http://localhost:8080/img/recipeImg/sushi3.jpg', '茄子', 8, 4, 25, '煮', '家常味', '10分钟', '初中水平', '3个', '2人份', '食盐,食用油,葱花', '适量,适量,5克', '炒茄子是一道以茄子、青椒、肉丝、姜丝、蒜泥、白糖、料酒、醋作为主要食材制作而成的美食，口味略带甜或咸味。脾胃虚寒、哮喘者不宜多吃', 'http://localhost:8080/img/successImg/success21.jpg');
INSERT INTO `recipearitice` VALUES (22, '心太软', 'http://localhost:8080/img/recipeImg/sushi4.jpg', '糯米饭,红枣', 10, 4, 10, '蒸', '甜味', '60分钟', '初级入门', '100克,适量', '2人份', '白糖,水,桂花', '50克,50毫升,5克', '心太软是用红枣、糯米制作的一道甜品。红枣富含蛋白质、脂肪、糖类、胡萝卜素、B族维生素、维生素C、维生素P以及钙、磷、铁和环磷酸腺苷等营养成分。具有美容，抗衰老作用的大枣，加上黏黏的糯米粉和白糖，红色的外表，包裹着白色的心儿，味道甜美，造型新颖。', 'http://localhost:8080/img/successImg/success22.jpg');
INSERT INTO `recipearitice` VALUES (23, '冰糖炖雪梨', 'http://localhost:8080/img/recipeImg/sushi5.jpg', '雪梨', 9, 4, 20, '煮', '甜味', '10分钟', '初级入门', '1个', '1人份', '冰糖,红枣,枸杞', '10克,10克,10克', '冰糖雪梨五月底出差回来就一直感冒,最近咳嗽的厉害,又不想吃药,于是给自己炖了个冰糖雪梨。冰糖雪梨具有甘甜可口,生津养胃,和中止咳的功效。', 'http://localhost:8080/img/successImg/success23.jpg');
INSERT INTO `recipearitice` VALUES (24, '西红柿炒鸡蛋', 'http://localhost:8080/img/recipeImg/sushi6.jpg', '西红柿,鸡蛋', 7, 4, 12, '炒', '家常味', '5分钟', '初中水平', '适量,适量', '2人份', '食盐,食用油,葱花', '适量,适量,5克', '西红柿炒鸡蛋，又名番茄炒蛋，是许多百姓家庭中一道普通的大众菜肴。烹饪方法简单易学，营养搭配合理。色泽鲜艳，口味宜人，爽口、开胃，深受大众喜爱。其营养价值丰富，具有营养素互补的特点以及健美抗衰老的作用。同时，也应当注意西红柿忌与石榴、螃蟹、冰棒、红薯、白酒、猪肝、鱼肉等物同食。', 'http://localhost:8080/img/successImg/success24.jpg');
INSERT INTO `recipearitice` VALUES (25, '土豆丝卷饼', 'http://localhost:8080/img/recipeImg/zaocan1.jpg', '面粉,土豆丝', 4, 8, 11, '煎', '香辣味', '15分钟', '初级入门', '100克,20克', '3人份', '食用油,辣椒酱', '3毫升,适量', '土豆丝卷饼是一道家常菜，制作原料主要有土豆、彩椒、蒜苗等，口味鲜美，营养丰富', 'http://localhost:8080/img/successImg/success25.jpg');
INSERT INTO `recipearitice` VALUES (26, '土豆饼', 'http://localhost:8080/img/recipeImg/zaocan2.jpg', '土豆条,中筋面粉', 6, 8, 54, '煎', '家常味', '15分钟', '初级入门', '100克,50克', '3人份', '葱花,盐,十三香,胡椒粉,食用油', '5克,3克,2克,2克,5毫升', '土豆饼加鸡蛋和不加鸡蛋口感不太一样。不加鸡蛋的有土豆丝的原香。可以根据个人喜好选择加或者不加。', 'http://localhost:8080/img/successImg/success26.jpg');
INSERT INTO `recipearitice` VALUES (27, '营养早餐烤鸡蛋', 'http://localhost:8080/img/recipeImg/zaocan3.jpg', '鸡蛋', 4, 8, 12, '烤', '家常味', '30分钟', '初级入门', '4个', '2人份', '毛豆,培根,胡椒粉,椒盐粉,番茄酱,盐', '适量,适量,适量,适量,适量,适量', '突然想到以前放学路上，街边卖的烤鸡蛋，可是已经很久没有看到过了，今天就凭着记忆试做一下，感觉还不错呢，除了有点老，味道还好，哈！营养哇，做早餐一流呢！', 'http://localhost:8080/img/successImg/success27.jpg');
INSERT INTO `recipearitice` VALUES (28, '金色馒头片', 'http://localhost:8080/img/recipeImg/zaocan4.jpg', '馒头,鸡蛋', 8, 8, 22, '煎', '家常味', '10分钟', '新手尝试', '1个,2个', '2人份', '橄榄油,椒盐', '10勺,1/2勺', '金色的阳光金色的你\r\n早上醒来，就要迎接忙碌的一天了。当餐桌上投下一缕温暖的金色阳光时，一家人享受着你营养快捷的早餐美食，这是多么的满足和幸福。来来来，一起来分享这款简单易做，香酥松脆，色泽金黄的炸馒头片吧，大人小孩都喜欢。', 'http://localhost:8080/img/successImg/success28.jpg');
INSERT INTO `recipearitice` VALUES (29, '银耳红枣汤', 'http://localhost:8080/img/recipeImg/zaocan5.jpg', '银耳,红枣,枸杞', 3, 8, 50, '煮', '咸鲜味', '10分钟', '初级入门', '1个,2克,10克', '1人份', '水,冰糖', '1升,15克', '银耳红枣汤是以银耳和红枣为主要食材制成的一款家常汤品，可适当添加莲子、枸杞子等配料。具有宁心安神、益智健脑、增强食欲功效。', 'http://localhost:8080/img/successImg/success29.jpg');
INSERT INTO `recipearitice` VALUES (30, '竹笋炒腊肉', 'http://localhost:8080/img/recipeImg/wucan1.jpg', '竹笋,腊肉', 7, 9, 20, '炒', '香辣味', '10分钟', '中级掌勺', '500克,150克', '5人份', '油,盐,酱油,红辣椒,蒜', '6毫升,3克,1毫升,2个,1个', '竹笋是大自然给人类的礼物馈赠，竹笋的营养丰富，含膳食纤维高、低热量、是减肥养生的好食材。竹笋能凉拌、炒、煲汤、还能晒干。最喜欢常吃的还是竹笋炒腊肉，让腊肉的香融入到竹笋中，口口嫩香。', 'http://localhost:8080/img/successImg/success30.jpg');
INSERT INTO `recipearitice` VALUES (31, '五彩皮冻', 'http://localhost:8080/img/recipeImg/wucan2.jpg', '猪肉皮,土豆,胡萝卜,青豆,黄豆,木耳', 3, 9, 50, '煮', '咸鲜味', '60分钟', '初中水平', '450克,80克,50克,80克,80克,30克', '5人份', '盐,香叶,桂皮,大料,花椒,葱姜,老抽,生抽,白糖', '5克,4克,5克,5克,4克,10克,15毫升,10毫升,10克', '喜欢吃肉皮冻，更喜欢食材多多，色彩斑斓，营养丰富的五彩皮冻。用买整块肉绞肉馅片下来的肉皮，做了这好看好吃，营养全面，尤其适合春季食用的五彩皮冻。', 'http://localhost:8080/img/successImg/success31.jpg');
INSERT INTO `recipearitice` VALUES (32, '金平牛蒡', 'http://localhost:8080/img/recipeImg/wucan3.jpg', '牛蒡,胡萝卜', 3, 9, 15, '炒', '家常味', '5分钟', '新手尝试', '一根,半根', '2人份', '植物油,生抽,白糖,白芝麻,葱花', '适量,少许,少许,少许,少许', '牛蒡是一种营养价值极高的蔬菜，它不仅富含膳食纤维，而且富含多种营养素，多酚类物质，经常食用牛蒡，能够起到促进消化、减脂瘦身、降血糖、降血压、降血脂、保肝护肝等等作用。金平是日式料理中常用的一种烹饪方法，有炒制的意思。', 'http://localhost:8080/img/successImg/success32.jpg');
INSERT INTO `recipearitice` VALUES (33, '法式芝士香蕉可丽饼', 'http://localhost:8080/img/recipeImg/xiawucha1.jpg', '低筋面粉,牛奶,酵母,糖,盐,玉米油', 3, 10, 102, '煎', '奶香味', '5分钟', '初中水平', '50克,100克,3克,6克,2克,少许', '1人份', '百吉福芝士,香蕉', '1片,1个', '以前做过的香蕉可丽饼没有加芝士，这次的简直香味爆棚啊！\r\n依然是无泡打粉的健康版，这个作为早餐营养全面了，有鸡蛋、牛奶、芝士、香蕉、碳水化合物。\r\n芝士就是香，确实与众不同，加入了芝士的可丽饼营养和香味都增加了不少。', 'http://localhost:8080/img/successImg/success33.jpg');
INSERT INTO `recipearitice` VALUES (34, ' \r\n山楂雪丽球', 'http://localhost:8080/img/recipeImg/xiawucha2.jpg', '山楂,白砂糖,吉利丁片', 3, 10, 43, '炒', '酸甜味', '2小时', '新手尝试', '500克,250克,5克', '5人份', '糖粉', '20克', '山楂雪丽球，小时候常见的一款零食，酸酸甜甜的，小小一个，一罐能吃上小半天。家里的5斤山楂终于做完了，今年应该是不会再做山楂的零食了', 'http://localhost:8080/img/successImg/success34.jpg');
INSERT INTO `recipearitice` VALUES (35, '旺旺旺红豆吐司', 'http://localhost:8080/img/recipeImg/xiawucha3.jpg', '高筋面粉,牛奶,水,炼奶,鸡蛋,盐', 5, 10, 65, '烘培', '甜味', '60分钟', '中级掌勺', '200克,35克,50克,10克,25克,2克', '3人份', '杏仁,巧克力', '6个,9个', '红豆具有清热解毒、健脾益胃、利尿消肿、通气除烦、补血生乳等多种功效。\r\n我会经常熬一些红豆放在冰箱随吃随拿，红豆可以做成蜜红豆也可以做成红豆沙', 'http://localhost:8080/img/successImg/success35.jpg');
INSERT INTO `recipearitice` VALUES (36, '蒸苜蓿麦饭', 'http://localhost:8080/img/recipeImg/wancan1.jpg', '苜蓿,玉米面,小米面,中筋面粉', 3, 11, 24, '蒸', '香辣味', '5分钟', '初中水平', '500克,60克,60克,80克', '2人份', '小葱,辣椒面,蒜泥,芝麻,盐,糖,十三香', '适量,5克,适量,少许,3克,1克,3克', '少油少盐，低热量低脂肪的食物\r\n春来野菜香，野菜很受人们的喜爱，目前已有荠荠菜，苜蓿，白蒿，茵陈，还有“树上蔬菜”香椿等已经上市。苜蓿也叫草头，可清炒，凉拌，包饺子，煎鸡蛋饼，额娘最喜欢用来蒸麦饭，洗净拌上各种面粉，一蒸一炒，拌上油泼辣子蒜泥汁，入口细润，香软可口，少油少盐，低热量低脂肪的素食，太美味啦！喜欢的亲抓紧吃，错过了季节，又要等一年哦', 'http://localhost:8080/img/successImg/success36.jpg');
INSERT INTO `recipearitice` VALUES (37, '营养杂粮粥', 'http://localhost:8080/img/recipeImg/wancan2.jpg', '杂粮米', 8, 11, 55, '煮', '家常味', '60分钟', '中级掌勺', '120克', '2人份', '红糖', '50克', '作为上班族，朝九晚五那是必须的；作为设计类的加班族，加班熬夜也是必须的。虽然我不习惯吃夜宵，但总有吃夜宵的时候。尤其是加班到深夜时，饥肠辘辘啊，恨不得来碗热腾腾的面条、香喷喷的热粥~\r\n但我最爱的宵夜还是热乎乎、香喷喷的粥~~', 'http://localhost:8080/img/successImg/success37.jpg');
INSERT INTO `recipearitice` VALUES (38, '砂锅烤红薯', 'http://localhost:8080/img/recipeImg/wancan3.jpg', '红薯', 4, 11, 4, '烤', '甜味', '60分钟', '新手尝试', '5个', '3人份', '锡纸', '2张', '又到一年红薯季，邻居送来了好多的红薯，孩子说想吃烤红薯，正好最近入手了一款砂锅，据说可以经得起800度的高温，还可以干烧，于是我就拿它来烤了个红薯，漂亮吧！', 'http://localhost:8080/img/successImg/success38.jpg');
INSERT INTO `recipearitice` VALUES (39, '葱香玉米烙', 'http://localhost:8080/img/recipeImg/yexiao1.jpg', '玉米片', 7, 12, 77, '炸', '家常味', '30分钟', '中级掌勺', '200克', '2人份', '盐,五香粉,植物油,小葱', '2勺,1勺,1勺,1根', '为了满足肠胃，为了身体健康，今天给大家分享一道美味小吃【葱香玉米烙】。做法非常简单，用时短，分分钟搞定！调味可甜可咸，营养低脂。不仅能起到磨牙、抗饥的作用，还能预防心脏病、癌症、延缓衰老、防止皮肤病变、减轻动脉硬化、对抗眼睛老化，增强人的脑力和记忆力！别小瞧玉米，它可是主食界里营养价值、保健作用最高的！嗯，就酱紫！', 'http://localhost:8080/img/successImg/success39.jpg');
INSERT INTO `recipearitice` VALUES (40, '糖不甩', 'http://localhost:8080/img/recipeImg/yexiao2.jpg', '糯米粉', 2, 12, 75, '煮', '甜味', '10分钟', '新手尝试', '120克', '2人份', '温水,红糖,熟花生', '一碗,一勺,一把', '小吃街上永远都少不了一份美味的糖不甩，其实它本来是属于广东的一道经典甜品，因做法简单，开胃又不腻，香味悠远逐渐发扬到全国各地的小吃街，糖不甩如今已不是什么高大上的点心了，自己在家就可以简单的做出来。', 'http://localhost:8080/img/successImg/success40.jpg');
INSERT INTO `recipearitice` VALUES (41, '油炸臭豆腐', 'http://localhost:8080/img/recipeImg/yexiao3.jpg', '臭豆腐', 7, 12, 14, '炸', '咸鲜味', '10分钟', '新手尝试', '1盒', '4人份', '油,蒜蓉辣椒酱,甜面酱', '50毫升,少许,少许', '臭豆腐是一种极具特色的中华传统小吃，古老而传统，我们这里有一句老话，叫做“臭发鲜”也就是闻闻臭的要命，入口却鲜的让人令人欲罢不能。臭豆腐可蒸可炸，这是一道油炸臭豆腐，需要二次炸制过程才会外脆里嫩，原本有一点咸味，蘸上各式的酱料，味道更佳', 'http://localhost:8080/img/successImg/success41.jpg');
INSERT INTO `recipearitice` VALUES (42, '野菜这样吃味道超赞…蒸豌', 'http://localhost:8080/img/successImg/success42.jpg', '豌豆苗,面粉', 3, 26, 13, '蒸', '家常味', '30分钟', '未知', '500克,适量', '3人份', '盐,蒜瓣,蒜苗,生抽,陈醋,白糖,辣椒', '适量,10瓣,2根,2勺,2勺,1勺,1勺', '春天真是各种野菜上市的时候，春天的滋味不可错过。\r\n简单面粉拌一拌蒸一蒸，绝美滋味！', 'http://localhost:8080/img/successImg/success42.jpg');
INSERT INTO `recipearitice` VALUES (43, '麻辣水煮鱼', 'http://localhost:8080/img/successImg/success43.jpg', '鱼,豆芽,芹菜,蒜苗', 8, 26, 53, '炒', '家常味', '5分钟', '新手尝试', '1条,适量,适量,适量', '1人份', '盐,蒜瓣,蒜苗,生抽,陈醋,白糖,辣椒', '适量,10瓣,2根,2勺,2勺,1勺,1勺', '麻辣水煮鱼是川渝地区的一道色香味俱全的名肴，属于川菜或渝菜。此菜肉质细嫩，麻辣不燥，鲜香醇厚，回味无穷。原料选新鲜生猛活鱼，又充分发挥辣椒御寒、益气养血功效，烹调出来的肉质一点也不会变韧，口感滑嫩，油而不腻。', 'http://localhost:8080/img/successImg/success43.jpg');
INSERT INTO `recipearitice` VALUES (44, '蒸心里美萝卜丝', 'http://localhost:8080/img/successImg/success44.jpg', '心里美萝卜', 5, 39, 23, '蒸', '家常味', '10分钟', '未知', '250克,50克', '3人份', '盐,蒜,醋,生抽,香油', '3克,适量,10克,3克,2克', '河南人爱吃蒸菜。我更爱吃。尤其是这个季节，总是争各种青菜，或者是萝卜丝等等。少油少盐，吃起来又健康。蒸萝卜丝是我的大爱，原来在饭店里吃做的筋道又松散的萝卜丝。自己做弄不好都是成坨了。后来反复琢磨，调换用料。终于蒸出来筋道又松散的萝卜丝了。在这里把方法分享给大家，爱吃蒸菜的也是是吧。这个方法也可以用白萝卜丝，我是为了好看用的心里美萝卜。比例基本上是一样的。', 'http://localhost:8080/img/successImg/success44.jpg');
INSERT INTO `recipearitice` VALUES (45, '卤味拼盘', 'http://localhost:8080/img/successImg/success45.jpg', '小鸡腿,鸡蛋,毛豆,莲菜,卤水豆腐', 6, 29, 16, '卤', '五香味', '90分钟', '中级掌勺', '6个,5个,100克,1节,100克', '3人份', '五香卤肉包,生姜,料酒,海盐,冰糖,老抽', '1个,10克,10克,30克,10克,10克', '这几天在厨房做饭 炒菜简直就是一件非常头大的事件，每次都挥汗如雨，都不想炒菜吃了，不炒菜吃凉拌菜某个人意见大的很，今天一大早起来就说想吃鸡腿，我说那就买几个卤着吃，人家又说别忘了放几个鸡蛋进去；就这样在人家的安排下一锅卤味上桌啦', 'http://localhost:8080/img/successImg/success45.jpg');
INSERT INTO `recipearitice` VALUES (46, '腌制辣白菜', 'http://localhost:8080/img/successImg/success46.jpg', '白菜,苹果,雪梨,白萝卜', 7, 27, 23, '腌', '家常味', '90分钟', '新手尝试', '1颗,1个,1个,半根', '2人份', '糯米粉,水,辣椒粉,糖,盐,姜', '200克,150克,200克,2勺,3勺,30克', '每逢过年都会腌制一些菜，今天做个韩式辣白菜，做法很简单哦', 'http://localhost:8080/img/successImg/success46.jpg');
INSERT INTO `recipearitice` VALUES (47, '南瓜派', 'http://localhost:8080/img/successImg/success47.jpg', '低筋面粉,黄油,糖粉,纯牛奶', 3, 10, 75, '烘培', '奶香味', '2小时', '初中水平', '100克,50克,20克,30克', '3人份', '南瓜泥,炼乳,鸡蛋', '180克,30克,1个', '南瓜含有丰富的营养，在崇尚健康生活的今天，多吃一些南瓜、红薯、紫薯都是对身体特别好的。\r\n今天就来推荐一道好吃的南瓜派，做下午茶点、聚会待客都是不错的选择哦。', 'http://localhost:8080/img/successImg/success47.jpg');
INSERT INTO `recipearitice` VALUES (48, '椰蓉南瓜派', 'http://localhost:8080/img/successImg/success48.jpg', '低粉,糖粉,蛋黄', 7, 10, 79, '烘培', '甜味', '30分钟', '中级掌勺', '75克,20克,20克', '2人份', '鸡蛋液,奶油奶酪,牛奶,南瓜泥', '45克,38克,55克,100克', '年夜饭怎么少得了甜品呢？吃完了团圆饭，再来一份饭后甜点，同家人一起分享这甜蜜的一刻。', 'http://localhost:8080/img/successImg/success48.jpg');
INSERT INTO `recipearitice` VALUES (49, '牛肉条小食', 'http://localhost:8080/img/successImg/success49.jpg', '牛肉,葱,玉米粒,淀粉', 7, 5, 23, '炒', '家常味', '10分钟', '新手尝试', '150克,30克,20克,20克', '3人份', '胡萝卜,土豆,鸡蛋', '15克,1个,1个', '12个月以上\r\n牛肉泥里面提前加了圆葱啊、玉米粒啥的，再挤成牛肉条的样子。和单纯的牛肉条相比，它可要好嚼太多了，咀嚼能力稍差一点的孩子也都能试试。\r\n\r\n牛肉条小食【宝宝辅食】的做法', 'http://localhost:8080/img/successImg/success49.jpg');
INSERT INTO `recipearitice` VALUES (50, '海苔肉松卷', 'http://localhost:8080/img/successImg/success50.jpg', '蛋黄,牛,奶糖', 4, 5, 77, '烙', '家常味', '10分钟', '新手尝试', '2个,100克,10克', '3人份', '油,淀粉,柠檬,吐司,肉松,海苔', '20克,5克,适量,2片,20克,10片', '一口吃下去，肉松的香、海苔的鲜、沙拉的甜、吐司的饱腹感全都有了，像糖果一样的长度还适合孩子直接用手抓着吃，锻炼手握和咀嚼能力。', 'http://localhost:8080/img/successImg/success50.jpg');
INSERT INTO `recipearitice` VALUES (51, '迷你汉堡肉', 'http://localhost:8080/img/successImg/success51.jpg', '猪肉泥,奶酪,碎葱,面粉', 5, 5, 46, '烙', '家常味', '10分钟', '新手尝试', '140克,20克,40克,15克', '3人份', '鸡蛋,酱油', '1个,适量', '从内而外都是鲜鲜嫩嫩的，咬上一口肉香味立刻充斥着整个口腔，再慢慢融开，细细品味这种爽口的快乐。', 'http://localhost:8080/img/successImg/success51.jpg');
INSERT INTO `recipearitice` VALUES (52, '竹笋炒腊肉', 'http://localhost:8080/img/successImg/success52.jpg', '竹笋,腊肉', 7, 5, 22, '炒', '香辣味', '10分钟', '中级掌勺', '500克,150克', '5人份', '油,盐,酱油,红辣椒,蒜', '6毫升,3克,1毫升,2个,1个', '竹笋是大自然给人类的礼物馈赠，竹笋的营养丰富，含膳食纤维高、低热量、是减肥养生的好食材。竹笋能凉拌、炒、煲汤、还能晒干。最喜欢常吃的还是竹笋炒腊肉，让腊肉的香融入到竹笋中，口口嫩香。', 'http://localhost:8080/img/successImg/success52.jpg');
INSERT INTO `recipearitice` VALUES (53, '烤鸡排', 'http://localhost:8080/img/successImg/success53.jpg', '鸡胸肉', 3, 5, 48, '烤', '家常味', '15分钟', '初级入门', '2块', '2人份', '油,盐,酱油,红辣椒,蒜', '6毫升,3克,1毫升,2个,1个', '鸡排，是现在小吃店里很流行的一种油炸类食品，香味可谓是十里飘香，孩子们每次看见都要去吃。油炸食品毕竟不能吃多，在家里试试用烤箱烤鸡排，腌制好的鸡胸肉分别裹上淀粉、蛋液、面包糠，进烤箱烘烤，不需油炸，做出来的鸡排表皮香脆，内里鲜嫩，女儿原话说“好吃到飞起来了。。。”哈哈', 'http://localhost:8080/img/successImg/success53.jpg');
